import React, {useState, useEffect} from 'react';
import axios from "axios";
import "./testMeStyle.css"


function Forum() {
    let [inputValue, setInputValue] = useState("");
    const [conversation, setConversation] = useState([]); // To store the conversation history

    function handleInputChange(e) {
        setInputValue(e.target.value);
    }

    async function submitData() {
        try {
            const response = await axios.post('/askQuestion', {
                message: inputValue
            });

            if (response.status === 200) {
                inputValue += response.data;
                setConversation([...conversation, { user: inputValue, ai: response.data.modifiedMessage }]);
                setInputValue(""); // Clear the input field
            } else {
                alert("Server responded with a non-success status");
            }
        } catch (e) {
            alert("An error occurred: " + e.message);
        }
    }

    function handleKeyPress(e) {
        if (e.key === "Enter") {
            e.preventDefault();
            submitData();
        }
    }

    // ... rest of your component

    return (
        <div className="container">
            <div className="fakeScreen rounded-3 col-lg-10" id="terminalWindow" onClick={handleFakeScreenClick}>
                <div>
                    <p className="line1">Ask me a question</p>

                    {conversation.map((entry, index) => (
                        <div key={index}>
                            <span className="fs-5 fw-normal user-input">{entry.user}</span>
                            <span className="fs-5 fw-normal ai-response">{entry.ai}</span>
                        </div>
                    ))}

                    <span className="fs-5 fw-normal">
                        {inputValue}
                        <span className="cursor fw-bold fs-3">_</span>
                    </span>
                </div>
                <input
                    id="hiddenInput"
                    type="text"
                    value={inputValue} // Add this to control the input
                    className="invisibleInput"
                    onChange={handleInputChange}
                    onKeyDown={handleKeyPress}
                    autoComplete="off"
                    autoCorrect="off"
                    autoCapitalize="off"
                    spellCheck="false"
                />
            </div>
        </div>
    );
}

export default Forum;
