import React, { useState, useEffect } from 'react';
import axios from "axios";
import "./testMeStyle.css";

function Forum() {
    const [inputValue, setInputValue] = useState("");
    const [conversation, setConversation] = useState([]);
    const [isTyping, setIsTyping] = useState(false);

    function handleFakeScreenClick() {
        document.getElementById("hiddenInput").focus();
    }

    function handleInputChange(e) {
        setInputValue(e.target.value);
        setIsTyping(true); // User is typing
    }

    function handleKeyPress(e) {
        if (e.key === "Enter") {
            setConversation(conv => [...conv, { text: inputValue, isUser: true }]);
            e.preventDefault();
            setInputValue("");
            setIsTyping(false);
            submitData();
        }
    }

    async function submitData() {
        setIsTyping(true); // Simulate system typing
        try {
            const response = await axios.post('/askQuestion', {
                message: inputValue
            });

            setTimeout(() => { // Simulate a delay for response
                if (response.status === 200) {
                    alert(response.data.modifiedMessage);
                    setConversation(conv => [...conv, { text: response.data.modifiedMessage, isUser: false }]);
                } else {
                    alert("Server responded with a non-success status");
                }
                setIsTyping(false); // Response received, stop typing animation
            }, 1000); // Adjust delay as needed

        } catch (e) {
            alert("An error occurred: " + e.message);
            setIsTyping(false);
        }
    }

    useEffect(() => {
        const inputElement = document.getElementById("hiddenInput");
        const preventDefault = (e) => e.preventDefault();
        inputElement.addEventListener('mousedown', preventDefault);
        return () => {
            inputElement.removeEventListener('mousedown', preventDefault);
        };
    }, []);

    return (
        <div className="container">
            <div className="fakeScreen rounded-3 col-lg-10" id="terminalWindow" onClick={handleFakeScreenClick}>
                <div>
                    <p className="line1">Ask me a question</p>
                    <span className="fs-5 fw-normal">
                        {conversation.map((entry, index) => (
                            <div key={index} className={entry.isUser ? "userText" : "responseText"}>
                                {entry.text}
                            </div>
                        ))}
                        {isTyping && <div className="typingAnimation">Typing...</div>}
                        <span className="cursor fw-bold fs-3">_</span>
                    </span>
                </div>
                <input
                    id="hiddenInput"
                    type="text"
                    className={"invisibleInput"}
                    value={inputValue}
                    onChange={handleInputChange}
                    onKeyDown={handleKeyPress}
                    autoComplete="off"
                    autoCorrect="off"
                    autoCapitalize="off"
                    spellCheck="false"
                />
            </div>
        </div>
    );
}

export default Forum;
