import React, {useState, useEffect} from 'react';
import axios from "axios";
import "./testMeStyle.css"


function Forum() {


    const [inputValue, setInputValue] = useState("");
    const [conversation, setConversation] = useState([]);
    const [isTyping, setIsTyping] = useState(false);

    function handleFakeScreenClick() {
        document.getElementById("hiddenInput").focus();
    }

    function handleInputChange(e) {
        setInputValue(e.target.value);
    }

    function handleKeyPress(e) {
        if (e.key === "Enter" && inputValue.trim() !== "") {
            setConversation([...conversation, { text: inputValue, isUser: true }]);
            e.preventDefault();
            submitData();
            setInputValue(""); // Clear input after submitting
        }
    }


    async function submitData() {
        try {
            const response = await axios.post('/askQuestion', {
                message: inputValue
            });

            if (response.status === 200) {
                simulateTypingEffect(response.data);
            } else {
                alert("Server responded with a non-success status");
            }
        } catch (e) {
            alert("An error occurred: " + e.message);
        }
    }

    function simulateTypingEffect(responseText) {
        setIsTyping(true);
        let i = 0;
        const interval = setInterval(() => {
            if (i < responseText.length) {
                setConversation(conv => [...conv, { text: responseText.substring(0, i + 1), isUser: false }]);
                i++;
            } else {
                clearInterval(interval);
                setIsTyping(false);
            }
        }, 50); // 50 milliseconds for each character, adjust as needed
    }



    useEffect(() => {
        const inputElement = document.getElementById("hiddenInput");
        const preventDefault = (e) => e.preventDefault();
        inputElement.addEventListener('mousedown', preventDefault);
        return () => {
            inputElement.removeEventListener('mousedown', preventDefault);
        };
    }, []);


    return (<div className="container">
        <div className="fakeScreen rounded-3 col-lg-10" id="terminalWindow" onClick={handleFakeScreenClick}>
            <div>
                <p className="line1">Ask me a question</p>
                <span className="fs-5 fw-normal">
                        {conversation.map((entry, index) => (
                            <div key={index} className={entry.isUser ? "userText" : "responseText"}>
                                {entry.text}
                            </div>
                        ))}
                    <span className="userInput">{inputValue}<span className="cursor fw-bold fs-3">_</span></span>
                    </span>
            </div>
            {/* Your input field here */}
            {isTyping && <span className="typingIndicator">Typing...</span>}
        </div>
    </div>)
}

export default Forum
