            <div className="flex min-h-full flex-row items-end md:flex-col md:items-start">
                <div className="flex flex-col gap-y-2">
                    <div className="flex flex-row items-center gap-x-2">
                        <img alt="Company Logo" height={"36px"}    src={`${Logo}`} />
                        <h1 className="text-2xl font-semibold md:text-3xl">RecommendAI</h1>
                    </div>
                    <p className="text-base md:text-xl">Innovating How You Discover What You Love</p>
                </div>
                <p className="ml-auto text-xs font-light text-foreground/50 md:ml-0 md:mt-auto">(c) 2024 RecommendAI â€” All rights reserved</p>
            </div>
        </footer>
    );
}

export default Footer;
